import React, { useState } from 'react'
import { Image, message, Spin, Upload, Row, Col } from 'antd'

import { DraggerHeading, DraggerLabel, Label } from './style'
import ClipIcon from '../../assets/icons/clip.svg'
import ipfs from '../../services/ipfs'
import { IPFS_INFURA_URL } from '../../config'

const { Dragger } = Upload

const acceptedTypes = 'image/jpeg,image/jpg,image/png,image/gif'
const acceptedTypesArray = ['image/jpeg', 'image/jpg', 'image/png', 'image/gif']

const Uploader = ({ setImageHash, label, description }) => {
	const [uploading, setUploading] = useState(false)
	const [hash, setHash] = useState(null)

	const captureImage = file => {
		setUploading(true)
		// const file = e.target.files[0]
		const reader = new window.FileReader()
		reader.readAsArrayBuffer(file)
		reader.onloadend = () => {
			const bufferArray = Buffer(reader.result)
			// console.log('buffer', bufferArray)
			if (bufferArray) {
				ipfs.add(bufferArray)
					.then(result => {
						// console.log(result)
						message.success('Uploaded to IPFS')
						setHash(result.path)
						return setImageHash(result.path)
					})
					.catch(err => {
						message.error('Error uploading to IPFS')
						console.error(err)
					})
					.finally(() => setUploading(false))
			}
		}
	}
	const handleOnChange = ({ file }) => {
		if (file.status === 'uploading') {
			const target_copy = Object.assign({}, file)
			// console.log(target_copy.originFileObj)
			captureImage(target_copy.originFileObj)
		}

		// captureImage(file.originFileObj)
	}

	const beforeUpload = file => {
		const isAccepted = acceptedTypesArray.includes(file.type)
		if (!isAccepted) {
			message.error('You can only upload JPEG, PNG, JPG or GIF file!')
		}
		const isLt50MB = file.size / 1024 / 1024 < 50
		if (!isLt50MB) {
			message.error('Image must smaller than 50MB!')
		}
		return isAccepted && isLt50MB
	}
	return (
		<div>
			<Label htmlFor=''>{label}</Label>
			<Row gutter={[10, 10]} justify='center'>
				<Col flex='auto'>
					<Dragger
						name='Image'
						accept={acceptedTypes}
						customRequest={({ onSuccess }) => {
							onSuccess('ok')
						}}
						onChange={handleOnChange}
						multiple={false}
						showUploadList={false}
						beforeUpload={beforeUpload}
					>
						<div>
							{uploading ? (
								<Spin size='large' />
							) : (
								<img src={ClipIcon} alt='' height='32' />
							)}
						</div>
						<DraggerHeading>
							Choose an image from your computer
						</DraggerHeading>
						<DraggerLabel>
							{description}
							<br />
							JPEG, JPG, PNG, or GIF - 50MB file limit
						</DraggerLabel>
					</Dragger>
				</Col>
				<Col>
					{hash ? (
						<Image width={200} src={`${IPFS_INFURA_URL}/${hash}`} />
					) : (
						<Image
							width={200}
							src='data:image/png;base64,'
						/>
					)}
				</Col>
			</Row>
			<br />
		</div>
	)
}

export default Uploader
