import React, { Fragment, useContext, useState, useEffect } from 'react'
import { Button, Card, Divider, Spin, Image } from 'antd'
import FactoryContext from '../../context/factory/factoryContext'

import { useQuery } from 'react-query'

import { Link } from 'react-router-dom'

import { getAllCampaigns, getCampaignDetails } from '../../api/web3Api'
import { IPFS_INFURA_URL } from '../../config'

const ExplorePage = () => {
	const { web3, accounts, contract } = useContext(FactoryContext)

	const isReady =
		typeof web3 !== 'undefined' &&
		contract.length !== 0 &&
		accounts.length !== 0

	const info = useQuery(
		['deployed_campaigns'],
		() => getAllCampaigns(contract),
		{
			enabled: isReady,
			// refetchOnReconnect: false,
			// refetchOnWindowFocus: false,
			// refetchOnMount: false,
		}
	)

	const [myData, setMyData] = useState([])
	useEffect(() => {
		let isMounted = true
		if (info.data && info.data.length !== 0 && isReady) {
			// eslint-disable-next-line
			{
				let myCampaignsList = []
				// eslint-disable-next-line
				info.data.map(campaignAdd => {
					if (campaignAdd) {
						getCampaignDetails(web3, campaignAdd)
							.then(data => {
								// console.log(data)
								myCampaignsList.push(data)
								if (isMounted)
									setMyData(oldData => [...oldData, data])
							})
							.catch(err => console.log(err))
					}
					// eslint-disable-next-line
				})
				// console.log(myCampaignsList)
				// setMyData(myCampaignsList)
			}
		}
		return () => {
			isMounted = false
		}
		// eslint-disable-next-line
	}, [info.data])

	return (
		<div
			style={{
				display: 'flex',
				justifyContent: 'center',
				alignItems: 'center',
				flexDirection: 'column',
			}}
		>
			<div>
				{info.isLoading && <Spin />}
				{info.data && info.data.length === 0 && (
					<div className='Center' style={{ height: '100vh' }}>
						<h4 style={{ color: 'red' }}>
							No Campaigns available right now. Create your
							campaign here.
						</h4>
						<Link to='/create/campaign'>
							<Button type='primary'>Create campaign</Button>
						</Link>
					</div>
				)}

				{myData.length !== 0 &&
					myData.map((data, index) => {
						return (
							<Fragment key={index}>
								<Card hoverable>
									<Image
										// width={200}
										height={200}
										src={`${IPFS_INFURA_URL}/${data.imageHash}`}
										fallback='data:image/png;base64,'
									/>
									{/* <img
										src={data.imageHash}
										alt=''
										fallback='data:image/png;base64,'
									/> */}
									<h3>{data.title}</h3>
									<Link to={`/campaign/${info.data[index]}`}>
										<p
											style={{
												color: 'blue',
												textDecoration: 'underline',
											}}
										>
											{info.data[index]}
										</p>
									</Link>
									<Link to={`/campaign/${info.data[index]}`}>
										<Button type='default'>
											View details
										</Button>
									</Link>
								</Card>
								<Divider />
							</Fragment>
						)
					})}
			</div>
		</div>
	)
}

export default ExplorePage
